
public class Areas {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Calculate area of Rectangle, formula used Length*Breath 
		
				        int length=55;
				        System.out.println("length of the given rectangle is 55 metre");
						int breath =70;
						System.out.println("breath of the given rectangle is 70 metre ");
						int area = length*breath;
						System.out.println("area"   +length*breath);
						
						
						
						
						//Calculate area of Perimeter , formula used 2(Length*Breath )
						int leng=90;
				        System.out.println("length of the given rectangle is 90 metre");
						int bred =80;
						System.out.println("breath of the given rectangle is 80 metre ");
						int perimetre = 2*(leng*bred);
						System.out.println("perimetre"  +perimetre);
						
						

	}

}
